                "components": [
                    {
                        "self": "https://fitogram.atlassian.net/rest/api/3/component/10050",
                        "id": "10050",
                        "name": "Products"
                    }
                ],
                "timeoriginalestimate": null,
                "description": {
                    "version": 1,
                    "type": "doc",
                    "content": [
                        {
                            "type": "paragraph",
                            "content": [
                                {
                                    "type": "text",
                                    "text": "Provider fisioterapia-fornasaro claims that this morning (around 10.30) some clients suddenly could not book anymore because they could not choose any product from the BT."
                                }
                            ]
                        },
                        {
                            "type": "paragraph",
                            "content": [
                                {
                                    "type": "text",
                                    "text": "She checked the booking rules and realised that some products were not associated anymore to the right booking group. No changes had been made before that time."
                                }
                            ]
                        },
                        {
                            "type": "paragraph",
                            "content": [
                                {
                                    "type": "text",
                                    "text": "Is it possible to check if something strange happened to the account? "
                                },
                                {
                                    "type": "hardBreak"
                                },
                                {
                                    "type": "text",
                                    "text": "Is there an explanation I could give her?"
                                }
                            ]
                        },
                        {
                            "type": "paragraph",
                            "content": [
                                {
                                    "type": "text",
                                    "text": "She already amended the booking rules and she is not able to remember exactly which products were affected."
                                }
                            ]
                        },
                        {
                            "type": "paragraph",
                            "content": [
                                {
                                    "type": "text",
                                    "text": "Intercom: "
                                },
                                {
                                    "type": "text",
                                    "text": "https://app.intercom.com/a/apps/yzurgwh0/inbox/inbox/2804996/conversations/17756200029203",
                                    "marks": [
                                        {
                                            "type": "link",
                                            "attrs": {
                                                "href": "https://app.intercom.com/a/apps/yzurgwh0/inbox/inbox/2804996/conversations/17756200029203"
                                            }
                                        }
                                    ]
                                }
                            ]
                        }
                    ]
                },
